//area de import
import './App.css';
import HelloWorld from './components/HelloWorld'

function App() {
  //area de compilação

  const name = "Ricardo";
  const newName = name.toUpperCase()

  function sum(a,b){
    return a + b;
  }

  const url = 'https://via.placeholder.com/150'

  return (
    //forma de declarar classes no react é usando o className
    <div className="App">
      <h2>Alterando o JSX</h2>
      <p>Primeiro App de {newName}.</p>
      <p>Soma: {sum(2,3)}</p>
      <img src={url} alt="Minha Imagem"></img>
      <HelloWorld/>
    </div>
  );
}

//area de export
export default App;
