//area de import
import './App.css';
import SayMyName from './components/SayMyName';
import Pessoa from './components/Pessoa';
import Frase from './components/Frase';
import List from './components/List'


//area de compilação
function App() {

  const nome = "Maria"

  return (
    //forma de declarar classes no react é usando o className
    <div className="App">
      <h1>Testando CSS</h1>
      <Frase/>
      <Frase/>
      <SayMyName nome="Ricardo"/>
      <SayMyName nome="Daniel"/>
      <SayMyName nome={nome}/>
      <Pessoa nome="Bruno" idade="26" profissao="Programador" foto="https://via.placeholder.com/150"/>
      <List/>
    </div>
    //chamando o componente <HelloWorld/>
  );
}

//area de export
export default App;
